import React, { useState } from "react";
import { TextField, Button, Typography, Paper } from '@mui/material';
import FileBase from 'react-file-base64';
import { useDispatch } from 'react-redux';

import useStyles from './styles'
import { createPost } from "../../actions/";

const Form = () =>{
    const [postData, setPostData] = useState({ creator: '', title: '', message:'', tags: '', selectedFiles: ''});
    const classes = useStyles();
    const dispatch = useDispatch();

    const handleSubmit =() =>{
        e.preventDefault();

        dispatch(createPost(postData));
    }

    const clear = () => {

    }
    
    return (
        <Paper className= {classes.paper}>
            <form autoComplete="off" noValidate className= {'${classes.root} ${classes.form}'} onSubmit={handleSubmit}>
                <Typography variant="h6">inputing onto memories</Typography>
                <TextField name="creator" variant="outlined" label="Creator" fullWidth value={postData.creator} onChange={ (e) => setPostData({...postData, creator: e.target.value})}/>
                <TextField name="title" variant="outlined" label="title" fullWidth value={postData.title} onChange={ (e) => setPostData({...postData, title: e.target.value})}/>
                <TextField name="message" variant="outlined" label="message" fullWidth value={postData.message} onChange={ (e) => setPostData({...postData, message: e.target.value})}/>
                <TextField name="tags" variant="outlined" label="tags" fullWidth value={postData.tags} onChange={ (e) => setPostData({...postData, tags: e.target.value})}/>    
                <div className= {classes.fileInput}>
                    <FileBase type="file" multiple={false} onDone={({base64}) => setPostData({...postData, selectedFiles: base64})} />
                </div>
                <Button className={classes.buttonSubmit} variant="contained" color="primary" size="large" type="submit" fullWidth>submit</Button>
                <Button variant="contained" color="scondary" size="small" onClick={clear} fullWidth>clear</Button>
            </form>
        </Paper>
    );
}

export default Form;
